INSERT INTO `task` (`id`, `title`, `description`, `isAutomaticTest`, `backend`, `disallowedContent`) VALUES
    (1, 'Perfekte Zahl', 'Eine <strong>natürliche Zahl</strong> <em>n</em> heißt perfekt oder vollkommen, wenn die Summe ihrer Teiler gleich n ist. Dabei sind die Teiler echt kleiner als <em>n</em>\r\n<p>. Beispielsweise ist 6 = 1 + 2 + 3 eine perfekte Zahl.</p>\r\n<p>&nbsp;</p>\r\nGeben Sie einen Algorithmus als <strong>Pseudocode</strong> an, der für eine gegebene Zahl bestimmt, ob sie perfekt ist.', 0, NULL, NULL),
    (2, 'Kubikwurzel nach Heron', 'Erweitern Sie die Lösungsidee für die Berechnung der Quadratwurzel nach Heron (siehe Vorlesungsfolien, Teil01, Folie 10) für die Berechnung der Kubikwurzel. Beschreiben Sie diesen Algorithmus im <strong>Pseudocode</strong>\r\n<p>.</p>\r\n<p>&nbsp;</p>\r\n<p>\r\nTipp:</p>\r\nAnstatt der Länge der Seite eines Quadrats mit Flächeninhalt <em>a</em> (Quadratwurzel in Vorlesung) benötigt man für die dritte Wurzel von <em>v</em> die Länge der Seite eines Kubus mit Rauminhalt <em>v</em>. Entwerfen Sie einen Algorithmus <strong>kubik</strong>, der einen Quader solange verändert bis annähernd alle Seiten gleich lang sind.', 0, NULL, NULL),
    (4, 'Primzahl', '<ol type="a">\r\n<li>Schreiben Sie eine Java-Methode \r\n<br /><br /><strong>public static boolean isPrime(int n)</strong>,<br /><br />\r\ndie möglichst effizient (siehe Blatt0, Aufgabe Primzahl) entscheidet, ob eine <strong>natürliche Zahl</strong> <em>n</em> eine Primzahl ist. Die Methode soll <em>true</em> zurückgeben, falls <em>n</em> eine Primzahl ist, im anderen Fall <em>false</em>.\r\n<p>&nbsp;</p>\r\n</li><li>Schreiben Sie eine weitere Methode \r\n<br /><br /><strong>public static int nextPrime(int n)</strong>,<br /><br />\r\ndie für eine  <strong>natürliche Zahl</strong> <em>n</em> die kleinste Primzahl liefert, die größer oder gleich <em>n</em> ist.</li></ol>', 1, 'java', '{"disallowedContent":["java.io","java.nio","java.net","javax.net"]}'),
    (5, 'Backus-Naur-Form (BNF)', '<ol type="a" start="1">\r\nSchreiben Sie in Backus-Naur-Form (BNF) gültige deutsche und amerikanische Datumsangaben (erst Monat, dann Tag, siehe unten).\r\n<br />Hinweis: Zur Vereinfachung werden folgende Annahmen getroffen:\r\n<ul>\r\n<li>Alle Monate können 31 Tage haben, die Tage 1 bis 9 werden ohne führende Null angegeben.</li>\r\n<li>Monatsangaben werden ebenfalls ohne führende Null geschrieben.</li>\r\n<li>Die Jahresangabe sei grundsätzlich vierstellig.</li>\r\n<li>Es ist sowohl die deutsche als auch amerikanische Schreibweise zulässig.</li>\r\n<li>Gültige Datumsangaben sind:\r\n<br />3.11.2006\r\n<br />9.1.2007\r\n<br />11-3-2006 oder 11/3/2006\r\n<br />1-9-2007 oder 1/9/2007\r\n<br />2006-11-3 oder 2006/11/3\r\n<br />2007-1-9 oder 2007/1/9\r\n</li></ul>\r\n</ol>', 0, NULL, NULL),
    (11, 'Befreundete Zahlen', '<p>Als befreundete Zahlen (amicable numbers) werden Paare natürlicher Zahlen (n, m) mit n ≠ m bezeichnet, für die gilt:</p>\r\n<p>\r\nDie Summe der echten Teiler von n ergibt m und Summe der echten Teiler von m ergibt n.</p>\r\n<p>\r\nBitte beachten Sie, dass auch 1 als Teiler von n betrachtet und mitsummiert wird, die Zahl n selbst aber nicht; beispielsweise ist die Summe der Teiler von 6 gleich 1 + 2 + 3.</p>\r\n<p>\r\nSchreiben Sie eine Java-Methode</p>\r\n<strong>public static int[][] amicablePairs(int n),</strong>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\ndie <em>n</em>\r\n<p> Paare befreundeter Zahlen liefert (Ergebnis ist eine zweispaltige Matrix).</p>\r\n<p>&nbsp;</p>\r\nHinweise:\r\n<ul>\r\n<li>Bei der Berechnung der befreundeten Paare ist darauf zu achten, dass die erste Zahl im jeweiligen Tupel die kleinere ist und keine Dubletten auftauchen.</li>\r\n<li>Beachten Sie beim Testen, dass die Ermittlung der befreundeten Paare mitunter viel Rechenzeit benötigt. Es ist daher ratsam, das <em>n</em> recht <strong>klein</strong> zu halten.</li>\r\n<li>Diskutieren Sie dabei verschiedene Implementierungsvarianten. </li></ul>', 0, NULL, NULL),
    (12, 'Perfektes Mischen', 'Sogenanntes perfektes Mischen eines Kartenstapels mit einer <strong>geraden</strong>\r\n<p>\r\nAnzahl von Karten funktioniert wie folgt:</p>\r\nDer Kartenstapel wird in der Mitte geteilt, dann werden die beiden Hälften so ineinander gemischt, dass die Karten aus den beiden Hälften genau abwechselnd im gemischten Stapel vorkommen.\r\n<p>&nbsp;</p>\r\n<p>Beispiel: Der ursprüngliche Stapel enthalte Zehn, Bube,Dame, König, As, Joker. Die zwei Hälften enthalten dann einerseits Zehn, Bube, Dame und andererseits König, As, Joker. Nach dem Zusammenfügen ergibt sich die gemischte Reihenfolge Zehn, König, Bube, As, Dame, Joker.</p>\r\n<p>Der Einfachheit halber repräsentieren wir den Kartenstapel dabei als Feld\r\nvon natürlichen Zahlen.</p>\r\n<ol type="a" start="1">\r\n<li>Schreiben Sie zunächst eine  Methode\r\n<br /><strong>public static int[] interleave(int[] a1, int[] a2)</strong>,<br />\r\ndie zwei als gleich lang vorausgesetzte Felder <em>a1</em> und <em>a2</em> akzeptiert (muss nicht überprüft werden) und ein Feld zurückgibt, in dem die Elemente aus <em>a1</em> und <em>a2</em> abwechselnd vorkommen, wobei mit dem ersten Element von <em>a1</em> begonnen wird.\r\nBeispiel\r\n<pre>int[] a1 = {1,2,3};\r\nint[] a2 = {4,5,6};\r\nint [] c = interleave(a,b); c enthält die Elemente 1,4,2,5,3,6</pre>\r\n</li>\r\n<li>Schreiben Sie eine  weitere Methode \r\n<br /><strong>public static int[] perfectMerge(int[] a)</strong>,<br /> \r\ndie perfektes Mischen für Felder mit einer geraden Anzahl von Elementen realisiert. Dazu muss zunächst das übergebene Feld in der Mitte geteilt werden und danach <em>interleave</em> aufgerufen werden.<br />\r\nBeispiel:\r\n<pre>int[] a = {1,2,3,4}\r\nperfectMerge(a) ergibt dann ein Feld mit den Elementen 1,3,2,4</pre>\r\n</li>\r\n<li>\r\nWird ein Kartenspiel oft genug perfekt gemischt, kehrt es in seine ursprüngliche Reihenfolge zurück. Schreiben Sie nun eine Methode \r\n<br /><strong>public static int mergeNumber(int n)</strong>, <br />\r\ndie eine beliebige gerade Zahl grösser Null als Grösse eines Kartenstapels akzeptiert und die zurückgibt, wie oft ein Kartenstapel dieser Größe höchstens perfekt gemischt werden muss, damit er seine ursprüngliche Reihenfolge wieder annimmt (wobei mindestens einmal gemischt werden muss).\r\n<br />\r\nBeispiel:\r\n<pre>mergeNumber(52) ergibt 8</pre>\r\n</li></ol>', 0, NULL, NULL),
    (13, 'Rekursive Funktion interpretieren I', 'Es ist folgende Funktion gegeben:\r\n<pre>Algorithm: f(x,y)\r\nInput:     x,y ganzzahlig\r\nOutput:    ?\r\n   if y = 0 then x\r\n            else f(x-1, y-1)\r\n   fi</pre>\r\n<ol type="a" start="1">\r\n<li>Beschreiben Sie die Auswertung an mehreren Beispielen. Was berechnet diese Funktion? Für welche Werte terminiert der Algorithmus nicht?</li>\r\n<li>Überlegen Sie wie der Algorithmus zu erweitern ist, damit er für <em>alle</em> ganzzahligen Werte terminiert. Schreiben Sie dafür eine Java-Methode \r\n<br /><br /><strong>public static int rek(int x, int y)</strong>.<br /><br /></li>\r\n<li>Formulieren Sie nun Ihren verbesserten Algorithmus als <em>iterative</em> Methode\r\n<br /><br /><strong> public static int iter(int x, int y)</strong>.<br /><br /> </li></ol>', 0, NULL, NULL),
    (14, 'Rekursive Funktion interpretieren II', '<ol type="a" start="1">\r\n\r\n<li>Gegeben sei der folgende rekursive Algorithmus für ganze Zahlen <em>n</em>:\r\n<pre>Algorithm: f(n)\r\nInput:     n ganzzahlig\r\nOutput:    ?\r\n   if n = 1  then 1\r\n             else f(n-1) + 2*n - 1\r\n   fi</pre>\r\nErläutern Sie anhand jeweils eines kurzen Beispiels, welche Berechnung der Algorithmus in den folgenden beiden Fällen ausführt:\r\n<br /><br /><em>n &gt; 0  </em> und <em>n &lt; = 0</em><br /><br />\r\n</li>\r\n<li>Gegeben sei der folgende rekursive Algorithmus für ganze Zahlen <em>x</em> und <em>y</em> (div bedeutet ganzzahlige Division):\r\n<pre>Algorithm: f(x, y)\r\nInput:     x, y ganzzahlig\r\nOutput:    ?\r\n   if y = 0  then 1\r\n             else if(x &lt; 2 * y) then f(x, x - y)\r\n                                else x * f(x - 1, y - 1) div y\r\n                  fi\r\n   fi</pre>\r\n<ul>\r\n<li>Erläutern Sie anhand der Beispiele <em>f(4,2)</em> und <em>f(8,5)</em>, welche Berechnungen der Algorithmus ausführt.</li>\r\n<li>Für welche <em>x</em> und <em>y</em> terminiert der Algorithmus nicht?</li>\r\n<li>Was ist die Semantik des Algorithmus?</li></ul>\r\n</li></ol>', 0, NULL, NULL),
    (15, 'Hornerschema', 'Reelle Polynome beliebigen Grades <em>n</em> können als Arrays der Länge <em>n + 1</em>\r\n<p>\r\nrepräsentiert werden:</p>\r\n<p>&nbsp;</p>\r\n<img src="polynom.png" alt="" width="90%/" />\r\n<p>&nbsp;</p>\r\n<ol type="a" start="1">\r\n<li>Schreiben Sie eine Methode\r\n<br /><br /><strong>public static double evalSimple(double[] a, double x)</strong>,<br /><br />\r\ndie durch sukzessive Summation der Ausdrücke <em>a<sub>i</sub>x<sup>i</sup></em>, <em>0</em><em>≤i</em><em>≤n</em> den Funktionswert <em>f(x)</em> für eine Variable <em>x </em>berechnet. Wieviele Schleifendurchläufe werden benötigt (in Abhängigkeit von n)?\r\n<br />\r\nHinweis: Sie dürfen nur Addition und Multiplikation verwenden.</li>\r\n\r\n<li>Schreiben Sie eine weitere iterative Methode\r\n<br /><br /><strong>public static double evalHorner(double[] a, double x)</strong>,<br /><br />\r\nzur Berechnung von <em>f(x)</em> nach dem Hornerschema, d. h. nach der Formel\r\n<p><em>f(x) = (((...((a<sub>n</sub>x + a<sub>n-1</sub>)x + a<sub>n-2</sub>)...)x + a<sub>2</sub>)x + a<sub>1</sub>)x + a<sub>0</sub>.</em></p>\r\n</li><li>Bestimmen Sie die Anzahl der Schleifendurchläufe, die mit dem Horner-Verfahren benötigt werden. Vergleichen Sie die Algorithmen aus <em>a)</em> und <em>b)</em> miteinander.</li>\r\n<li>Schreiben Sie eine rekursive Variante des Hornerschemas als Methode\r\n<br /><br /><strong>public static double evalHornerRek(double[] a, double x)</strong><br /><br /></li></ol>', 0, NULL, NULL),
    (16, 'Variante der Ackermann-Funktion', 'Die folgende Funktion berechnet <strong>eine Version</strong> (nach Abelson, Sussman: Structure and Interpretation of Computer Programs) der mathematischen Funktion mit dem Namen <em>Ackermann</em>:\r\n<pre>Algorithm Ackermann (x, y):\r\n   Input: natuerliche Zahlen x, y\r\n   Output: Ackermann-Zahl\r\n   \r\n   if (y = 0) then 0\r\n   if (x = 0) then 2*y\r\n   if (y = 1) then 2\r\n   else Ackermann(x-1, Ackermann(x, y-1))</pre>\r\n<ol type="1">\r\n<li>Schreiben Sie eine Java-Methode\r\n<br /><br /> <strong>public static BigInteger ackermann(BigInteger x, BigInteger y)</strong><br /><br />\r\nzur Berechnung der Ackermann-Zahl. Hinweis: Informieren Sie sich über die Klasse <em>BigInteger</em> in der <a class="external-link" href="http://download.oracle.com/javase/6/docs/api/index.html">Java-API</a> (http://download.oracle.com/javase/6/docs/api/index.html).</li>\r\n<li>Welches sind die Werte der Methodenaufrufe <em>ackermann(1, 10), ackermann(2, 4), ackermann(3, 3)</em>?\r\n</li>\r\n\r\n<li>Die Klasse <em>BigInteger</em> dient der Verarbeitung beliebig großer ganzer Zahlen. Warum gibt es bei der Berechnung der Ackermann-Zahl trotzdem Probleme?</li></ol>', 0, NULL, NULL),
    (17, 'Fibonacci-Zahlen', 'Eine besondere Folge von Zahlen sind die Fibonacci-Zahlen, die rekursiv definiert werden können als\r\n<p>&nbsp;</p>\r\n<table>\r\n<tbody>\r\n<tr>\r\n<td><em>fib</em>(x) =</td>\r\n<td>if (x = 0) ∨ (x = 1) then 1</td>\r\n</tr>\r\n<tr>\r\n<td></td>\r\n<td>else <em>fib</em>(x-2) + <em>fib</em>(x-1)</td>\r\n</tr>\r\n<tr>\r\n<td></td>\r\n<td>fi</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<ol type="a" start="1">\r\n<li>Schreiben Sie eine rekursive Methode \r\n<br /><br /><strong>public static BigInteger fib1(int n)</strong><br /><br />\r\nzur Berechnung der x-ten Fibonacci-Zahl.\r\nGeben Sie im Hauptprogramm die ersten 15 Fibonacci-Zahlen aus.</li>\r\n<li>Zeigen Sie am Beispiel <em>fib1(5)</em>, wie die Anzahl der Aufrufe dieser Methode wächst (Baumrekursion) (Lösung als Block-Kommentar).</li>\r\n<li>Formulieren Sie eine iterative Methode \r\n<br /><br /><strong>public static BigInteger fib2(int n)</strong><br /><br />\r\nzur Berechnung der Fibonacci-Zahlen und zeigen Sie am Beispiel <em>fib2(5)</em>, dass <em>fib2</em> effizienter arbeitet als <em>fib1</em>.\r\n<br /> Hinweis: Merken Sie sich jeweils die beiden Vorgänger in gesonderten Variablen.</li>\r\n<li>Berechnen Sie innerhalb der main-Methode nach beiden Varianten die Fibonacci-Zahl von <em>n = 23</em> und zählen Sie dabei die notwendigen Schleifendurchläufe.</li></ol>', 0, NULL, NULL),
    (18, 'Russische Bauernmultiplikation', 'Die Russische Bauernmultiplikation (auch Ägyptisches Multiplizieren oder Abessinische Bauernregel genannt) ist ein einfaches Verfahren zur Multiplikation zweier <strong>natürlicher Zahlen</strong>.\r\n<p>\r\n\r\nEs war schon im Altertum bekannt, in Deutschland wurde es bis ins Mittelalter verwendet. In Russland war es bis weit in die Neuzeit üblich, daher der Name.</p>\r\nDas Verfahren hat den Vorteil, dass man im Prinzip nur halbieren, verdoppeln und addieren muss.\r\n<div>&nbsp;\r\n<p>Die Berechnung erfolgt nach folgendem Algorithmus:</p>\r\n<ol type="1" start="1">\r\n<li>Man schreibt die beiden zu multiplizierenden Zahlen nebeneinander.</li>\r\n<li>Auf der linken Seite werden die Zahlen jeweils halbiert (Reste abgerundet) und die Ergebnisse untereinander geschrieben, bis man zur 1 gelangt.</li>\r\n<li>Auf der rechten Seite werden die Zahlen jeweils verdoppelt und untereinander geschrieben.</li>\r\n<li>Die rechts stehenden (verdoppelten) Zahlen werden gestrichen, wenn die links stehende Zahl gerade ist.</li>\r\n<li>Die Summe der nicht gestrichenen rechts stehenden Zahlen ergibt das gesuchte Produkt.</li></ol>\r\n<p>\r\nÜberprüfen Sie diesen Algorithmus an einem selbst gewählten Beispiel.</p>\r\nSchreiben Sie in Java eine&nbsp;<strong>iterative</strong>&nbsp;Methode&nbsp;<br /><br /><strong>public static int farmerMultIter(int x, int y)</strong><br /><br />und eine&nbsp;<strong>rekursive</strong>&nbsp;Methode&nbsp;<br /><br /><strong>public static int farmerMultRek(int x, int y)</strong><br /><br />für diesen Algorithmus.\r\n<div>&nbsp;</div>\r\n</div>', 0, NULL, NULL),
    (19, 'Tabbelchen gibt Anweisungen', '1A', 0, NULL, 'null'),
