    (14, 'import org.junit.Test;\r\nimport static org.junit.Assert.assertTrue;\r\nimport static org.junit.Assert.assertFalse;\r\nimport static org.junit.Assert.assertEquals;\r\n\r\npublic class PrimeTest {\r\n\r\n    @Test\r\n    public void isPrime() {  \r\n        int tmp = (int) (999 * Math.random())+1; \r\n        if (myIsPrime(tmp))\r\n assertTrue("wrong result isPrime("+tmp+")",Prime.isPrime(tmp));  \r\n        else\r\n   assertFalse("wrong result isPrime("+tmp+")",Prime.isPrime(tmp));\r\n        assertTrue("wrong result isPrime(2)",Prime.isPrime(2));\r\n        assertTrue("wrong result isPrime(13)",Prime.isPrime(13));\r\n        assertTrue("wrong result isPrime(1009)",Prime.isPrime(1009));\r\n\r\n        assertFalse("wrong result isPrime(1)",Prime.isPrime(1));\r\n        assertFalse("wrong result isPrime(128)",Prime.isPrime(128));\r\n    }\r\n\r\n    @Test\r\n    public void nextPrime() {\r\n        int tmp = (int) (999 * Math.random())+1;  \r\n        assertEquals("nextPrime("+tmp+")",myNextPrime(tmp),Prime.nextPrime(tmp));\r\n        assertEquals("nextPrime(13)",13,Prime.nextPrime(13));\r\n        assertEquals("nextPrime(655)",659,Prime.nextPrime(655));\r\n        assertEquals("nextPrime(2)",2,Prime.nextPrime(2));\r\n        assertEquals("nextPrime(1)",2,Prime.nextPrime(1));\r\n    }\r\n\r\n    public static boolean myIsPrime(int n) {\r\n        if (n == 1)\r\n            return false; \r\n        if (n == 2)\r\n            return true; \r\n        if (n % 2 == 0)\r\n            return false; \r\n        int i = 3;\r\n        while (i*i <= n){\r\n            if (n % i == 0){\r\n                return false; \r\n            }\r\n            else\r\n                i = i + 2; \r\n        }       \r\n        return true;\r\n    }\r\n\r\n    public static int myNextPrime(int zahl) {\r\n        if (zahl==1) return 2;\r\n        if (myIsPrime(zahl))\r\n            return zahl;\r\n        if (zahl % 2 == 0)\r\n            zahl++;\r\n        while (!myIsPrime(zahl)) {\r\n            zahl += 2;\r\n        }\r\n        return zahl;\r\n    }\r\n\r\n}', '2014-03-16 12:18:32', 'PrimeTest', 'java', '12ca603b8f562dd486eecc8e921b562b6ea91d48'),
    (15, 'import static org.junit.Assert.*;\r\n\r\nimport org.junit.Test;\r\nimport java.util.Arrays;\r\n\r\npublic class MedianTest {\r\n\r\n    private static int myMedian(int a, int b, int c) {\r\n        int[] f = {a, b, c};\r\n        Arrays.sort(f);\r\n        return f[1];\r\n    }\r\n\r\n    @Test\r\n    public void testMedian() {\r\n        int tmp1 = (int) (999 * Math.random()) + 1; // random number in\r\n                                                    // [1..1000]\r\n        int tmp2 = (int) (999 * Math.random()) + 1;\r\n        int tmp3 = (int) (999 * Math.random()) + 1;\r\n        assertEquals("median(" + tmp1 + "," + tmp2 + "," + tmp3 + ")", myMedian(tmp1, tmp2, tmp3), Median.median(tmp1, tmp2, tmp3));\r\n        assertEquals("median(" + tmp1 + "," + tmp1 + "," + tmp1 + ")", tmp1, Median.median(tmp1, tmp1, tmp1));\r\n        assertEquals("median(" + tmp1 + "," + tmp2 + "," + tmp1 + ")", myMedian(tmp1, tmp2, tmp1), Median.median(tmp1, tmp2, tmp1));\r\n        assertEquals("median(" + tmp2 + "," + tmp1 + "," + tmp1 + ")", myMedian(tmp2, tmp1, tmp1), Median.median(tmp2, tmp1, tmp1));\r\n\r\n        assertEquals("median(1, 2, 1)", 1, Median.median(1, 2, 1));\r\n        assertEquals("median(2, 1, 1)", 1, Median.median(2, 1, 1));\r\n        assertEquals("median(1, 1, 1)", 1, Median.median(1, 1, 1));\r\n        assertEquals("median(1, 55, 999)", 55, Median.median(1, 55, 999));\r\n        assertEquals("median(1, 999, 55)", 55, Median.median(1, 999, 55));\r\n        assertEquals("median(55, 1, 999)", 55, Median.median(55, 1, 999));\r\n        assertEquals("median(55, 999, 1)", 55, Median.median(55, 999, 1));\r\n        assertEquals("median(999, 55, 1)", 55, Median.median(999, 55, 1));\r\n        assertEquals("median(999, 1, 55)", 55, Median.median(999, 1, 55));\r\n\r\n        assertEquals("median(-1,  1,  0)", 0, Median.median(-1, 1, 0));\r\n        assertEquals("median(-5, -1, -3)", -3, Median.median(-5, -1, -3));\r\n        assertEquals("median( 3, -1, -4)", -1, Median.median(3, -1, -4));\r\n    }\r\n\r\n    @Test\r\n    public void testMedian2() {\r\n        assertEquals("median2(1, 2, 1)", 1, Median.median2(1, 2, 1));\r\n        assertEquals("median2(2, 1, 1)", 1, Median.median2(2, 1, 1));\r\n        assertEquals("median2(1, 1, 1)", 1, Median.median2(1, 1, 1));\r\n        assertEquals("median2(1, 55, 999)", 55, Median.median2(1, 55, 999));\r\n        assertEquals("median2(1, 999, 55)", 55, Median.median2(1, 999, 55));\r\n        assertEquals("median2(55, 1, 999)", 55, Median.median2(55, 1, 999));\r\n        assertEquals("median2(55, 999, 1)", 55, Median.median2(55, 999, 1));\r\n        assertEquals("median2(999, 55, 1)", 55, Median.median2(999, 55, 1));\r\n        assertEquals("median2(999, 1, 55)", 55, Median.median2(999, 1, 55));\r\n\r\n        assertEquals("median2(-1,  1,  0)", 0, Median.median2(-1, 1, 0));\r\n        assertEquals("median2(-5, -1, -3)", -3, Median.median2(-5, -1, -3));\r\n        assertEquals("median2( 3, -1, -4)", -1, Median.median2(3, -1, -4));\r\n    }\r\n\r\n}\r\n', '2014-03-29 22:00:01', 'MedianTest', 'java', 'cea17ce764fd57754f6139b4f9adf3f0191bd293'),
