    (27, '\r\n\r\npublic class NPalindrome {\r\n\r\n    public static int numDigits(int n) {\r\n        return Integer.toString(n).length();\r\n    }\r\n\r\n    public static int getDigit(int n, int index) {\r\n        if (index < 0) {\r\n            return 0;\r\n        }\r\n\r\n        String tmp = Integer.toString(n);\r\n        if (index >= tmp.length())\r\n            index = tmp.length() - 1;\r\n        return Character.getNumericValue(tmp.charAt(tmp.length() - index - 1));\r\n    }\r\n\r\n    public static boolean isPalindrome(int n) {\r\n        char[] input = Integer.toString(n).toCharArray();\r\n        for (int i = 0, j = input.length - 1; i <= j && j >= i; ++i, --j) {\r\n            if (input[i] != input[j])\r\n                return false;\r\n        }\r\n        return true;\r\n\r\n    }\r\n\r\n    public static void main(String[] args) {\r\n        System.out.println(numDigits(1));\r\n        System.out.println(numDigits(10));\r\n        System.out.println(numDigits(3));\r\n        System.out.println(numDigits(321));\r\n\r\n        System.out.print(getDigit(102020, 0));\r\n        System.out.print(getDigit(102020, 1));\r\n        System.out.print(getDigit(102020, 2));\r\n        System.out.print(getDigit(102020, 3));\r\n        System.out.print(getDigit(102020, 4));\r\n        System.out.println(getDigit(102020, 5));\r\n\r\n        System.out.println(getDigit(101, -2));\r\n        System.out.println(getDigit(101, 5));\r\n\r\n        System.out.println(isPalindrome(1001));\r\n        System.out.println(isPalindrome(10001));\r\n        System.out.println(isPalindrome(10002));\r\n        System.out.println(isPalindrome(20001));\r\n        System.out.println(isPalindrome(2001));\r\n        System.out.println(isPalindrome(1002));\r\n\r\n    }\r\n}', '2014-04-17 15:33:15', 'NPalindrome', 'java', '9697837c1aaa681d5a3fc3da16bcc2799491d84b'),
    (28, '\r\npublic class NPalindrome {\r\n\r\n    public static int numDigits(int n) {\r\n        return Integer.toString(n).length();\r\n    }\r\n\r\n    public static int getDigit(int n, int index) {\r\n        if (index < 0)\r\n            index = 0;\r\n\r\n        String tmp = Integer.toString(n);\r\n        if (index >= tmp.length())\r\n            index = tmp.length() - 1;\r\n        return Character.getNumericValue(tmp.charAt(tmp.length() - index - 1));\r\n    }\r\n\r\n    public static boolean isPalindrome(int n) {\r\n        char[] input = Integer.toString(n).toCharArray();\r\n        for (int i = 0, j = input.length - 1; i <= j && j >= i; ++i, --j) {\r\n            if (input[i] != input[j])\r\n                return false;\r\n        }\r\n        return true;\r\n\r\n    }\r\n\r\n    public static void main(String[] args) {\r\n        System.out.println(numDigits(1));\r\n        System.out.println(numDigits(10));\r\n        System.out.println(numDigits(3));\r\n        System.out.println(numDigits(321));\r\n\r\n        System.out.print(getDigit(102020, 0));\r\n        System.out.print(getDigit(102020, 1));\r\n        System.out.print(getDigit(102020, 2));\r\n        System.out.print(getDigit(102020, 3));\r\n        System.out.print(getDigit(102020, 4));\r\n        System.out.println(getDigit(102020, 5));\r\n\r\n        System.out.println(getDigit(101, -2));\r\n        System.out.println(getDigit(101, 5));\r\n\r\n        System.out.println(isPalindrome(1001));\r\n        System.out.println(isPalindrome(10001));\r\n        System.out.println(isPalindrome(10002));\r\n        System.out.println(isPalindrome(20001));\r\n        System.out.println(isPalindrome(2001));\r\n        System.out.println(isPalindrome(1002));\r\n\r\n    }\r\n}', '2014-04-17 15:34:18', 'NPalindrome', 'java', '6898bd7f35cdfba808eafee571db55db730be0d7'),
